import React, { FC } from 'react'
import { Page as PdfPage } from '@react-pdf/renderer'
import compose from '../styles/compose'

interface Props {
  className?: string
  pdfMode?: boolean
}

const Page: FC<Props> = ({ className, pdfMode, children }) => {
  return (
    <>
      {pdfMode ? (
        <PdfPage size="A4" style={{
          fontFamily: 'Nunito',
          fontSize: '13px',
          color: '#555',
          padding: '40px 35px',
        }}>
          {children}
        </PdfPage>
      ) : (
        <div className={'page invoice-wrapper'}>{children}</div>
      )}
    </>
  )
}

export default Page
