import { CSSClasses } from '../data/types'

const colorDark = '#222'
const colorDark2 = '#666'
const colorGray = '#e3e3e3'
const colorWhite = '#fff'

const styles: CSSClasses = {
  dark: {
    color: colorDark,
  },




  'p-4-8': {
    padding: '4px 8px',
  },

  'p-5': {
    padding: '5px',
  },

  'pb-10': {
    paddingBottom: '10px',
  },

  right: {
    textAlign: 'right',
  },

  bold: {
    fontWeight: 'bold',
  },

  'fs-20': {
    fontSize: '20px',
  },

  'fs-45': {
    fontSize: '45px',
  },

  page: {
    fontFamily: 'Nunito',
    fontSize: '13px',
    color: '#555',
    padding: '40px 35px',
  },

  'invoice-wrapper': {
    position: 'relative',
    backgroundColor: '#fff',
    padding: '40px 35px',
    boxShadow: '0 0 17px 0 rgba(16, 40, 73, 0.09)'
  },

  span: {
    padding: '4px 12px 4px 0',
  },

  logo: {
    display: 'block',
  }
}

export default styles
